import React from 'react';

function TaskList({ tasks, onUpdateStatus, onDelete }) {
  return (
    <ul>
      {tasks.map((task) => (
        <li key={task._id}>
          <h3>{task.title}</h3>
          <p>{task.description}</p>
          <p>Prioridad: {task.priority}</p>
          <p>Estado: {task.status}</p>
          <button onClick={() => onUpdateStatus(task._id)}>Cambiar estado</button>
          <button onClick={() => onDelete(task._id)}>Eliminar</button>
        </li>
      ))}
    </ul>
  );
}

export default TaskList;
