import React, { useState } from 'react';
import { createTask, updateTask } from '../services/api';

function TaskForm({ task, onTaskSaved }) {
  const [title, setTitle] = useState(task ? task.title : '');
  const [description, setDescription] = useState(task ? task.description : '');
  const [priority, setPriority] = useState(task ? task.priority : 'Medium');
  const [dueDate, setDueDate] = useState(task ? task.dueDate : '');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (task) {
        await updateTask(task._id, { title, description, priority, dueDate });
      } else {
        await createTask({ title, description, priority, dueDate });
      }
      onTaskSaved();
    } catch (error) {
      console.error("Error guardando tarea:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Titulo" value={title} onChange={(e) => setTitle(e.target.value)} required />
      <textarea placeholder="Descripcion" value={description} onChange={(e) => setDescription(e.target.value)} required />
      <select value={priority} onChange={(e) => setPriority(e.target.value)}>
        <option value="Low">Baja</option>
        <option value="Medium">Media</option>
        <option value="High">Alta</option>
      </select>
      <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} />
      <button type="submit">{task ? "Actualizar tarea" : "Crear tarea"}</button>
    </form>
  );
}

export default TaskForm;
