import React, { useEffect, useState } from 'react';
import { fetchTasks } from '../services/api';
import TaskForm from '../components/TaskForm';
import TaskList from '../components/TaskList';

function Dashboard() {
  const [tasks, setTasks] = useState([]);
  const [filteredTasks, setFilteredTasks] = useState([]); 
  const [priorityFilter, setPriorityFilter] = useState('Todas');
  const [editingTask, setEditingTask] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const loadTasks = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetchTasks();
      setTasks(response.data);
      setFilteredTasks([...response.data]);
    } catch (error) {
      console.error("Error cargando tareas:", error);
      setError("Error al cargar tareas.");
    } finally {
      setLoading(false);
    }
  };

  
  useEffect(() => {
    console.log("Prioridad cambiada:", priorityFilter);

    if (priorityFilter === "Todas") {
      setFilteredTasks([...tasks]);
    } else {
      const newFilteredTasks = tasks.filter(task => task.priority === priorityFilter);
      setFilteredTasks([...newFilteredTasks]);
    }
  }, [priorityFilter, tasks]);

  return (
    <div className='task-frame'>

      <br></br><br></br>
      <h2>Dashboard</h2>
      
      <br></br><br></br>
      {loading && <p>Loading tasks...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <label>Filtrar por prioridad:</label>
      <select value={priorityFilter} onChange={(e) => setPriorityFilter(e.target.value)}>
        <option value="Todas">Todas</option>
        <option value="Baja">Baja</option>
        <option value="Media">Media</option>
        <option value="Alta">Alta</option>
      </select>
      <br></br> <br></br>

      <TaskForm task={editingTask} onTaskSaved={loadTasks} />
      
      <TaskList tasks={tasks} onTaskUpdated={setEditingTask} onTaskDeleted={loadTasks} />
    </div>
  );
}

export default Dashboard;
