import React from 'react';
import { deleteTask } from '../services/api';

function TaskList({ tasks, onTaskUpdated, onTaskDeleted }) {
  const handleDelete = async (id) => {
    if (window.confirm("Estas seguro de que quieres eliminar esta tarea?")) {
      await deleteTask(id);
      onTaskDeleted();
    }
  };

  return (
    <ul>
      {tasks.map(task => (
        <li key={task._id}>
          <h3>{task.title}</h3>
          <p>{task.description}</p>
          <p>Prioridad: {task.priority}</p>
          <button onClick={() => onTaskUpdated(task)}>Editar</button>
          <button onClick={() => handleDelete(task._id)}>Eliminar</button>
          <br></br> <br></br>
        </li>
      ))}
    </ul>
  );
}

export default TaskList;
